// Hole QA-Antwort
const qa = $input.item.json;
const content = qa.choices?.[0]?.message?.content || '{}';

// Hole Daten vom vorherigen Normalisieren-Node Ã¼ber $node
const prevData = $node['ðŸ§© Normalisieren'].json;
const baseDraft = prevData.draft;
const company = prevData.company;
const ticker = prevData.ticker;
const sector = prevData.sector;
const date = prevData.date;
const filename = prevData.filename;
const heroImage = prevData.heroImage;

let score = 5, issues = 'unbekannt', improved = '';

// Parse QA response
try {
  let jsonStr = content;
  const codeBlockMatch = content.match(/```json?([\s\S]*?)```/);
  if (codeBlockMatch) {
    jsonStr = codeBlockMatch[1];
  }
  const parsed = JSON.parse(jsonStr.trim());
  score = parsed.quality_score ?? score;
  issues = parsed.issues || 'Keine';
  improved = parsed.improved_content || '';
} catch (e) {
  const scoreMatch = content.match(/quality_score[\"']?\s*[:=]\s*(\d+)/i);
  if (scoreMatch) score = parseInt(scoreMatch[1]);
  issues = 'QA-JSON nicht parsebar';
}

const finalContent = (score < 7 && improved && improved.length > 100) ? improved : baseDraft;

return {
  json: {
    company,
    ticker,
    sector,
    date,
    filename,
    heroImage,
    qualityScore: score,
    issues,
    content: finalContent,
    qaRaw: content.substring(0, 500)
  }
};

const d = $input.item.json;

// Formatiere die fertige Analyse als kopierbaren Text
const output = `
=====================================
FERTIGE ANALYSE - KOPIERBEREIT
=====================================

Unternehmen: ${d.company}
Ticker: ${d.ticker}
Sektor: ${d.sector}
Datum: ${d.date}
Quality Score: ${d.qualityScore}/10
Issues: ${d.issues}

-------------------------------------
INHALT:
-------------------------------------

${d.content}

-------------------------------------
DATEIEN:
-------------------------------------
Filename: ${d.filename}.html
Hero Image: ${d.heroImage}

=====================================
`;

return { json: {
  status: 'ok',
  qualityScore: d.qualityScore,
  issues: d.issues,
  company: d.company,
  ticker: d.ticker,
  sector: d.sector,
  date: d.date,
  filename: d.filename,
  content: d.content,
  formattedOutput: output
}};
