const body = $input.item.json.body || $input.item.json;

const company = body.company || 'Tesla';
const ticker = body.ticker || 'TSLA';
const sector = body.sector || 'Zyklischer Konsum';
const date = body.date || new Date().toISOString().split('T')[0];
const baseDir = $env.BASE_DIR || '/workspaces/capitovo';
const slug = (body.filename || `${company}-${ticker}`)
  .toLowerCase()
  .replace(/[^a-z0-9]+/g, '-')
  .replace(/(^-|-$)/g, '');

return { json: { company, ticker, sector, date, baseDir, slug } };

