function mdToHtml(md) {
  return md
    .replace(/^### (.*)$/gm, '<h3>$1</h3>')
    .replace(/^## (.*)$/gm, '<h2>$1</h2>')
    .replace(/^# (.*)$/gm, '<h1>$1</h1>')
    .replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>')
    .replace(/^- (.*)$/gm, '<li>$1</li>')
    .replace(/(<li>.*<\/li>)/gs, '<ul>$1</ul>')
    .replace(/

/g, '</p><p>')
    .replace(/
/g, '<br>');
}

const meta = $('Prepare Input').item.json;
const htmlContent = mdToHtml(content);
const summary = `${meta.company} Equity-Research-Bericht`;

return {
  json: {
    meta,
    content: htmlContent,
    raw: content,
    summary
  }
};

