const entry = {
  id: `${meta.ticker.toLowerCase()}-${meta.date}` ,
  category: meta.sector,
  title: `${meta.company}: Equity-Research-Bericht`,
  summary: summary,
  link: `Abonenten/${meta.slug}.html`,
  image: `data/vorschaubilder/${meta.slug}.svg`,
  date: meta.date,
  author: 'capitovo Research',
  tags: [meta.company, meta.ticker, meta.sector],
  published: true
};
analysen.unshift(entry);
fs.writeFileSync(jsonPath, JSON.stringify(analysen, null, 2), 'utf8');

// Generate simple SVG placeholder
