var d = $input.item.json;

return {
  json: {
    ...d,
    htmlPath: 'Abonenten/' + d.slug + '.html',
    svgPath: 'data/vorschaubilder_analysen/' + d.slug + '_preview.svg',
    htmlBase64: Buffer.from(d.html, 'utf8').toString('base64'),
    svgBase64: Buffer.from(d.svg, 'utf8').toString('base64'),
    htmlCommitMsg: 'Analyse: ' + d.company + ' (' + d.ticker + ') - ' + d.date,
    svgCommitMsg: 'Vorschaubild: ' + d.company + ' - ' + d.date
  }
};

var d = $('Base64 Encoder').item.json;

return {
  json: {
    status: 'PUBLISHED',
    message: 'Apple-Analyse erfolgreich veroeffentlicht!',
    company: d.company,
    ticker: d.ticker,
    date: d.date,
    liveUrl: 'https://kw-f8.github.io/capitovo/' + d.htmlPath,
    svgUrl: 'https://kw-f8.github.io/capitovo/' + d.svgPath,
    files: {
      html: d.htmlPath,
      svg: d.svgPath
    },
    catalogEntry: {
      id: d.ticker.toLowerCase() + '-' + d.date.replace(/-/g, ''),
      category: d.sector,
      title: d.company + ' - Unternehmensanalyse',
      summary: 'Analytische Einordnung von ' + d.company + ' (' + d.ticker + ')',
      link: d.htmlPath,
      image: d.svgPath,
      date: d.date,
      author: 'capitovo Research'
    }
  }
};
